import "../styles/pages/Home.css";
import imgRoadmap from "../assets/Home/imgRoadmap.svg"
import { useNavigate } from 'react-router-dom';

function Home() {
    const navigate = useNavigate();

    const handleClick = () => {
        navigate('/Roadmap');
    };

    const [currentIndex, setCurrentIndex] = useState(0);

  // Lista de itens do carrossel (pode ser imagens, textos, etc.)
  const items = [
    { id: 1, content: 'Item 1' },
    { id: 2, content: 'Item 2' },
    { id: 3, content: 'Item 3' },
    { id: 4, content: 'Item 4' },
  ];

  // Função para avançar para o próximo item
  const nextItem = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % items.length);
  };

  // Função para voltar para o item anterior
  const prevItem = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + items.length) % items.length);
  };

    return (
        <div className="Home">
            <div className="card">
                <div className="esquerda">
                    <h1>ROADMAP</h1>
                    <p className="textHome">Com nosso gerador de roadmaps
                        você somente precisa indicar a sua
                        vaga desejada e ele irá montar um
                        plano de estudos para que você
                        possa focar somente no aprendizado.
                    </p>
                    <button onClick={handleClick} className="btnGerar">GERAR</button>
                </div>
                <div className="direita">
                    <img src={imgRoadmap} className="imgRoadmap" />
                </div>
            </div>
        </div>
    );
}

export default Home;
